import React from 'react';

class SignIn extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			signInEmail: '',
			signInPassword: '',
			wrongCreds: false
		}
	}

	onEmailChange = (event) => {
		this.setState({signInEmail: event.target.value})
	}

	onPasswordChange = (event) => {
		this.setState({signInPassword: event.target.value})
	}

	// onSubmitSignIn = () => {
	// 	// fetch('http://localhost:3000/signin', {
	// 	// 	method: 'post',
	// 	// 	headers: {'Content-Type': 'application/json'},
	// 	// 	body: JSON.stringify({
	// 	// 		email: this.state.signInEmail,
	// 	// 		password: this.state.signInPassword,
	// 	// 	})
	// 	// })
	// 		// .then(response => response.json())
	// 		// .then(user => {
	// 		// 	if(user.id) {
	// 		// 		this.props.loadUser(user);
	// 				this.props.onRouteChange('home')
	// 	// 		} else {
	// 	// 			this.setState({wrongCreds: true})
	// 	// 		}
	// 	// 	}
	// 	// )
	//  }

	render() {
		const { onRouteChange } = this.props;
		return ( 
			<div className="center pt4">
				{ (this.state.wrongCreds) ? 
					<div className="flex items-center justify-center pa4">
					  <span className="dark-blue lh-title ml4">Wrong username or password! Try Again</span>
					</div> :
					<div></div>}
				<article className="debug-grid br3 ba dark-gray b--black-10 mv4 w-200 w-50-m w-25-l mw6 shadow-5 center">
					<main className="pa4 black-80">
					  <div className="measure">
					    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
					      <legend className="dark-blue f1 fw6 ph0 mh0">Sign In</legend>
					      <div className="mt3">
					        <label className="dark-blue db fw6 lh-copy f6" htmlFor="email-address">Email</label>
					        <input 
					        	className="dark-blue pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
					        	type="email" 
					        	name="email-address"  
					        	id="email-address"
					        	onChange={this.onEmailChange}
					         />
					      </div>
					      <div className="mv3">
					        <label className="dark-blue db fw6 lh-copy f6" htmlFor="password">Password</label>
					        <input 
					        	className="dark-blue b pa2 input-reset ba bg-transparent hover-bg-dark-blue hover-white w-100"
					        	type="password" 
					        	name="password"  
					        	id="password"
					        	onChange={this.onPasswordChange}
					        />
					      </div>
					    </fieldset>
					    <div className="">
					      <input 
					      	onClick={() => onRouteChange('home')}
					      	className="dark-blue b ph3 pv2 input-reset ba b--dark-blue bg-transparent grow pointer f6 dib" 
					      	type="submit"
					      	value="Sign in" />
					    </div>
					    <div className="lh-copy mt3">
					      <p onClick={() => onRouteChange('register')} className="dark-blue f6 link grow black dim db pointer">Register</p>				  
					    </div>
					  </div>
					</main>
				</article>
			</div>
		)
	}
}

export default SignIn;
