import React from 'react';

class Cart extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			orderList: this.trimmed(props.cart)
		}
		console.log(this.state.orderList);
	}

	trimmed(cart) {
		cart.sort(this.compare);
		let newCart = [];
		cart.slice().reverse().forEach((item,index) => {
			if ( index != cart.length-1) {
				if ( item.name != cart[index+1].name )
					newCart.push(item);
			}	
		});
		newCart.push(cart[cart.length-1]);
		return newCart;
	}

	compare(a,b) {
	  if (a.name < b.name)
	    return -1;
	  if (a.name > b.name)
	    return 1;
	  return 0;
	}

	render() {
		return (
			<div>
				this is cart
			</div>
		)
	}
}

export default Cart;
